class {static get inherits(){return["Mrbr.System.Object","Mrbr.System.EventEmitter"]}static get manifest(){return[new Mrbr.System.ManifestEntry(Mrbr.System.ManifestEntry.FileTypes.Class,"Mrbr.System.Timers")]}constructor(...e){this.base(...e);const t=Mrbr.System.Timers.debounce(this.window_resize.bind(this),50,!1);window.addEventListener("resize",t)}dispose(){window.removeEventListener("resize",this.window_resize.bind(this))}static get width(){return window.innerWidth||document.documentElement.clientWidth||document.body.clientWidth}static get height(){return window.innerHeight||document.documentElement.clientHeight||document.body.clientHeight}static get size(){return{width:Mrbr.Browser.WindowControl.width,height:CanvasUI.Controls.WindowControl.height}}window_resize(e){var t=Mrbr.Browser.WindowControl;this.emit(Mrbr.Browser.WindowControl.eventNames.resize,this,{width:t.width,height:t.height})}static get eventNames(){return{resize:"windowControl_resize"}}}